var assert = require('assert');
var time = require('./time.js');

module.exports = {
    "test formatDate": function(){
        var test = new Date(1320501261362);//Sat, 05 Nov 2011 13:54:21 GMT
        assert.equal(time.formatDate(test, "yyyyMMddHHmmss"), "20111105215421", "test yyyyMMddHHmmss");
        assert.equal(time.formatDate(test, "yyyy-MM-dd HH:mm:ss"), "2011-11-05 21:54:21", "test yyyy-MM-dd HH:mm:ss");
        assert.equal(time.formatDate(test, "MM/dd/yyyy"), "11/05/2011", "test yyyy-MM-dd HH:mm:ss");
    }
};